import React, {useState} from 'react'
import PropTypes from 'prop-types'

export default function TextForms(props) {
    const changeToLow = ()=>{
        let vr = text.toLowerCase()
        newText(vr)
    }
    const changeToUp = ()=>{
        let variable = text.toUpperCase()
        newText(variable)
    }
    const toUpHandler = (event)=>{
        newText(event.target.value);
    }
    const [text,newText] = useState("Enter your Text Here..");
  return (
    <>
    <div className="container">
        <h3>{props.heading}</h3>
<div className="mb-3">
  <textarea className="form-control" id="exampleFormControlTextarea1" rows="8" value={text} onChange={toUpHandler}></textarea>
</div>
<button type="button" className="btn btn-primary" onClick={changeToUp}>Change Case to UpperCase</button>
<button type="button" className="btn btn-secondary" onClick={changeToLow}>convert to LowerCase</button>
    </div>

    <div className="container">
    <h4>Your Text Summary</h4>
    <p>Your text has {text.split(" ").length} words and {text.length} characters</p>
    </div>

    </>
  )
}

TextForms.propTypes={
    heading:PropTypes.string
}
TextForms.defaultProps={
    heading:"Heading here"
}
