import Alert from './components/Alert';
import TextForms from './components/TextForms';

function App() {

  const [alert,setAlert] = useState(null)

  const showAlert = (msg,Type)=>{
    setAlert({
      msg : msg,
      Type : Type
    })
  }

  const toggleMode = ()=>{
    if(mode==="light"){
      newMode("dark")
      newTxtCol("light")
      document.body.style.background = '#56585d'
      showAlert(
        "Dark Mode Enabled","Success"
      )
    }
    else{
      newMode("light")
      newTxtCol("dark")
      document.body.style.background = 'white'
      showAlert(
        "light Mode Enabled","danger"
      )
    }
  }

  const [txtCol,newTxtCol] = useState("dark")

  const [mode,newMode]=useState('light')
  return (
    <>

    <Navbar title="Text-Conv" nav2="about textUtils" mode={mode} toggleMode={toggleMode} txtCol={txtCol}/>
    <Alert mode={mode} alert={alert}/>
    <TextForms heading="Enter your Text to perform analyzation" mode={mode} txtCol={txtCol}/>
    {/* <About/> */}
    </>
  );
}

export default App;
